KNOWN_WEIGHT = 950from utime import sleepfrom hx711 import HX711from machine import freqfreq(80000000) driver = HX711(12, 14,1)def read():    driver.power_on()    while (driver.is_ready()):        pass    measure= driver.read(True)    return(measure)print("vaciar la celda de carga")sleep(5)tare = read()print("coloque un peso conocido")sleep(5)slope =(read()-tare)/KNOWN_WEIGHTprint(slope)while True:    weight = (read()-tare)/slope    print("%.1f g" % (weight))    sleep(0.2)